import React, { Component } from 'react'
import axios from 'axios'
import BusinessForm from './BusinessForm'
import BusinessCard from './BusinessCard'

class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      name: '',
      location: '',
      data: null,
    }
  }

  handleChange = (e) => {
    this.setState({ [e.target.name]: e.target.value })
  }

  handleSubmit = async (e) => {
    e.preventDefault()
    const { name, location } = this.state
    const res = await axios.post('http://localhost:5000/business-data', { name, location })
    this.setState({ data: res.data })
  }

  regenerateHeadline = async () => {
    const { name, location } = this.state
    const res = await axios.get(
      `http://localhost:5000/regenerate-headline?name=${name}&location=${location}`
    )
    this.setState(prev => ({
      data: { ...prev.data, headline: res.data.headline },
    }))
  }

  render() {
    const { name, location, data } = this.state
    return (
      <div className="min-h-screen bg-gray-100 p-6 flex items-center justify-center">
        <div className="w-full max-w-md bg-white rounded-2xl shadow-xl p-6">
          <h1 className="text-2xl font-bold text-center mb-4">Local Business Dashboard</h1>
          <BusinessForm
            name={name}
            location={location}
            onChange={this.handleChange}
            onSubmit={this.handleSubmit}
          />
          {data && (
            <BusinessCard
              data={data}
              onRegenerate={this.regenerateHeadline}
            />
          )}
        </div>
      </div>
    )
  }
}

export default App
